
public class AndOrOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x, y;
		x = 100;
		y = 50;
		if (x>0 & y>0)
		{
		System.out.println("Both are positive");
		}
		else if (x<0 & y<0)
		{
		System.out.println("Both are negative");
		}
		else
		{
			System.out.println("One is positive");
			
		}
		
		
		
		
		
				

	}

}
